    }

    render() {
        return(
            <div className="card">
                <form className="card-body">
                    <div className="from-group">
                        <input
                         type="text"
                         name="title"
                         onChange={this.handleInput}
                         className="from-control"
                         placeholder="title"
                        />
                    </div>
                    <div className="from-group">
                        <input
                        type="text"
                        name="responsible"
                        className="from-control"
                        placeholder="responsible"
                        />
                    </div>
                    <div className="from-group">
                        <input
                            type="text"
                            name="description"
                            className="from-control"
                            placeholder="description"
                        />
                    </div>
                    <div className="from-group">
                        <input
                            type="text"
                            name="priority"
                            className="from-control"
                            placeholder="priority"
                        />
                    </div>

                </form>

            </div>

        );
    }
}
export  default  TodoForm;
